INSERT INTO `jos_content` VALUES (2,68,'About Us','about-us','<div id=\"about_us\" class=\"clearfix\">\r\n<h4>Eye Centers of Ohio represents a new horizon in the delivery of comprehensive eye care services.</h4>\r\n<div class=\"left\">With six surgeons and two locations, we offer an exciting and unique combination of skills and resources we feel is unparalleled. When it comes to providing you the highest quality care, our vision remains crystal clear:\r\n<h3>Focus on excellence in eye care.</h3>\r\n<p>Thank you for your confidence and support, and we welcome your referrals at our current locations. Eye Centers of Ohio...the clear choice in eye care!</p>\r\n<span class=\"huge_banner\">It\'s Easy to See the Difference</span>\r\n<p class=\"signature\">- Your Doctors at Eye Centers of Ohio</p>\r\n</div>\r\n<img class=\"right\" src=\"images/doctorsGroupShot.png\" alt=\"\" /></div>','',1,9,'2014-11-06 05:39:44',42,'','2015-01-27 03:17:13',42,0,'0000-00-00 00:00:00','2014-11-06 05:39:44','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_articl