INSERT INTO `jos_ucm_history` VALUES (154,14,1,'','2015-01-27 05:43:33',42,2878,'41aeb805abcf7057e1be8df99eb846bda1f27896','{\"id\":14,\"asset_id\":82,\"title\":\"Schedule LASIK\",\"alias\":\"schedule-lasik\",\"introtext\":\"<div id=\\\"schedule_lasik\\\">\\r\\n<h3>Are you a candidate for LASIK?<\\/h3>\\r\\n<p>If you are at least 21 years old, in good general health and have no past history of previous eye diseases. LASIK can treat a very large range of refractive or focusing difficulties, including near (myopia) and far (hyperopia) sightedness and astigmatism.<\\/p>\\r\\n<p>Patients best suited for laser eye surgery are ones who are currently unhappy wearing glasses or contact lenses. Good LASIK candidates, however, must have realistic expectations. It is best to have a clear goal for the eye surgery (such as the ability to see more clearly without glasses or contacts) as well as a thorough understanding of the risks, possible complications, and limitations of laser vision correction. It is important to remember that, as with all surgeries, a good result cannot be guaranteed and no procedure is risk free.<\\/p>\\r\\n<p>To find out if LASIK surgery is right for you, schedule a free screening with our LASIK department. At the screening, we will confirm if you are a good candidate. A complete LASIK consultation with the surgeon carries a charge which is credited to the procedure should you proceed with surgery.<\\/p>\\r\\n<\\/div>\",\"fulltext\":\"\",\"state\":1,\"catid\":\"2\",\"created\":\"2015-01-27 05:43:33\",\"created_by\":\"42\",\"created_by_alias\":\"\",\"modified\":\"\",\"modified_by\":null,\"checked_out\":null,\"checked_out_time\":null,\"publish_up\":\"2015-01-27 05:43:33\",\"publish_down\":\"0000-00-00 00:00:00\",\"images\":\"{\\\"image_intro\\\":\\\"\\\",\\\"float_intro\\\":\\\"\\\",\\\"image_intro_alt\\\":\\\"\\\",\\\"image_intro_caption\\\":\\\"\\\",\\\"image_fulltext\\\":\\\"\\\",\\\"float_fulltext\\\":\\\"\\\",\\\"image_fulltext_alt\\\":\\\"\\\",\\\"image_fulltext_caption\\\":\\\"\\\"}\",\"urls\":\"{\\\"urla\\\":false,\\\"urlatext\\\":\\\"\\\",\\\"targeta\\\":\\\"\\\",\\\"urlb\\\":false,\\\"urlbtext\\\":\\\"\\\",\\\"targetb\\\":\\\"\\\",\\\"urlc\\\":false,\\\"urlctext\\\":\\\"\\\",\\\"targetc\\\":\\\"\\\"}\",\"attribs\":\"{\\\"show_title\\\":\\\"\\\",\\\"link_titles\\\":\\\"\\\",\\\"show_tags\\\":\\\"\\\",\\\"show_intro\\\":\\\"\\\",\\\"info_block_position\\\":\\\"\\\",\\\"show_category\\\":\\\"\\\",\\\"link_category\\\":\\\"\\\",\\\"show_parent_category\\\":\\\"\\\",\\\"link_parent_category\\\":\\\"\\\",\\\"show_author\\\":\\\"\\\",\\\"link_author\\\":\\\"\\\",\\\"show_create_date\\\":\\\"\\\",\\\"show_modify_date\\\":\\\"\\\",\\\"show_publish_date\\\":\\\"\\\",\\\"show_item_navigation\\\":\\\"\\\",\\\"show_icons\\\":\\\"\\\",\\\"show_print_icon\\\":\\\"\\\",\\\"show_email_icon\\\":\\\"\\\",\\\"show_vote\\\":\\\"\\\",\\\"show_hits\\\":\\\"\\\",\\\"show_noauth\\\":\\\"\\\",\\\"urls_position\\\":\\\"\\\",\\\"alternative_readmore\\\":\\\"\\\",\\\"article_layout\\\":\\\"\\\",\\\"show_publishing_options\\\":\\\"\\\",\\\"show_article_options\\\":\\\"\\\",\\\"show_urls_images_backend\\\":\\\"\\\",\\\"show_urls_images_frontend\\\":\\\"\\\",\\\"extra-class\\\":\\\"\\\"}\",\"version\":1,\"ordering\":null,\"metakey\":\"\",\"metadesc\":\"\",\"access\":\"1\",\"hits\":null,\"metadata\":\"{\\\"robots\\\":\\\"\\\",\\\"author\\\":\\\"\\\",\\\"rights\\\":\\\"\\\",\\\"xreference\\\":\\\"\\\"}\",\"featured\":\"0\",\"language\":\"*\",\"xreference\":\"\"}',0);
INSERT INTO `jos_ucm_history` VALUES (155,14,1,'','2015-01-27 05:44:27',42,3019,'9c4532864b18963050ea8720bcaa420dc18c082f','{\"id\":14,\"asset_id\":\"82\",\"title\":\"Schedule LASIK\",\"alias\":\"schedule-lasik\",\"introtext\":\"<div id=\\\"schedule_lasik\\\">\\r\\n<h3>Are you a candidate for LASIK?<\\/h3>\\r\\n<p>If you are at least 21 years old, in good general health and have no past history of previous eye diseases. LASIK can treat a very large range of refractive or focusing difficulties, including near (myopia) and far (hyperopia) sightedness and astigmatism.<\\/p>\\r\\n<p>Patients best suited for laser eye surgery are ones who are currently unhappy wearing glasses or contact lenses. Good LASIK candidates, however, must have realistic expectations. It is best to have a clear goal for the eye surgery (such as the ability to see more clearly without glasses or contacts) as well as a thorough understanding of the risks, possible complications, and limitations of laser vision correction. It is important to remember that, as with all surgeries, a good result cannot be guaranteed and no procedure is risk free.<\\/p>\\r\\n<p>To find out if LASIK surgery is right for you, <a href=\\\"index.php?option=com_content&amp;view=article&amp;id=9&amp;catid=12&amp;Itemid=111\\\">schedule a FREE screening<\\/a>\\u00a0with our LASIK department. At the screening, we will confirm if you are a good candidate. A complete LASIK consultation with the surgeon carries a charge which is credited to the procedure should you proceed with surgery.<\\/p>\\r\\n<\\/div>\",\"fulltext\":\"\",\"state\":1,\"catid\":\"2\",\"created\":\"2015-01-27 05:43:33\",\"created_by\":\"42\",\"created_by_alias\":\"\",\"modified\":\"2015-01-27 05:44:27\",\"modified_by\":\"42\",\"checked_out\":\"42\",\"checked_out_time\":\"2015-01-27 05:43:33\",\"publish_up\":\"2015-01-27 05:43:33\",\"publish_down\":\"0000-00-00 00:00:00\",\"images\":\"{\\\"image_intro\\\":\\\"\\\",\\\"float_intro\\\":\\\"\\\",\\\"image_intro_alt\\\":\\\"\\\",\\\"image_intro_caption\\\":\\\"\\\",\\\"image_fulltext\\\":\\\"\\\",\\\"float_fulltext\\\":\\\"\\\",\\\"image_fulltext_alt\\\":\\\"\\\",\\\"image_fulltext_caption\\\":\\\"\\\"}\",\"urls\":\"{\\\"urla\\\":false,\\\"urlatext\\\":\\\"\\\",\\\"targeta\\\":\\\"\\\",\\\"urlb\\\":false,\\\"urlbtext\\\":\\\"\\\",\\\"targetb\\\":\\\"\\\",\\\"urlc\\\":false,\\\"urlctext\\\":\\\"\\\",\\\"targetc\\\":\\\"\\\"}\",\"attribs\":\"{\\\"show_title\\\":\\\"\\\",\\\"link_titles\\\":\\\"\\\",\\\"show_tags\\\":\\\"\\\",\\\"show_intro\\\":\\\"\\\",\\\"info_block_position\\\":\\\"\\\",\\\"show_category\\\":\\\"\\\",\\\"link_category\\\":\\\"\\\",\\\"show_parent_category\\\":\\\"\\\",\\\"link_parent_category\\\":\\\"\\\",\\\"show_author\\\":\\\"\\\",\\\"link_author\\\":\\\"\\\",\\\"show_create_date\\\":\\\"\\\",\\\"show_modify_date\\\":\\\"\\\",\\\"show_publish_date\\\":\\\"\\\",\\\"show_item_navigation\\\":\\\"\\\",\\\"show_icons\\\":\\\"\\\",\\\"show_print_icon\\\":\\\"\\\",\\\"show_email_icon\\\":\\\"\\\",\\\"show_vote\\\":\\\"\\\",\\\"show_hits\\\":\\\"\\\",\\\"show_noauth\\\":\\\"\\\",\\\"urls_position\\\":\\\"\\\",\\\"alternative_readmore\\\":\\\"\\\",\\\"article_layout\\\":\\\"\\\",\\\"show_publishing_options\\\":\\\"\\\",\\\"show_article_options\\\":\\\"\\\",\\\"show_urls_images_backend\\\":\\\"\\\",\\\"show_urls_images_frontend\\\":\\\"\\\",\\\"extra-class\\\":\\\"\\\"}\",\"version\":2,\"ordering\":\"0\",\"metakey\":\"\",\"metadesc\":\"\",\"access\":\"1\",\"hits\":\"0\",\"metadata\":\"{\\\"robots\\\":\\\"\\\",\\\"author\\\":\\\"\\\",\\\"rights\\\":\\\"\\\",\\\"xreference\\\":\\\"\\\"}\",\"featured\":\"0\",\"language\":\"*\",\"xreference\":\"\"}',0);
INSERT INTO `jos_ucm_history` VALUES (156,15,1,'','2015-01-27 05:51:07',42,3820,'61caf0f89df5542f9fe724e98f42cc7193ecebf2','{\"id\":15,\"asset_id\":83,\"title\":\"Cataracts\",\"alias\":\"cataracts\",\"introtext\":\"<p>Cataracts occur as part of the normal aging process. Studies show that virtually everyone over age 65 has some cataract formation in their eyes! Cataracts can severely reduce your vision. At one time, cataracts were a leading cause of blindness in the world. But today, fortunately they can be treated. Modern surgical techniques, intraocular lens implantation and \\\"same day surgery\\\" make cataract surgery safe, fast and effective.<\\/p>\\r\\n<p>A cataract occurs when the normally clear lens of the eye becomes cloudy. As the cataract develops, the cloudiness no longer allows the lens to properly focus light on the back of the eye. This unfocused light causes the vision to look blurry or hazy. Development of cataracts has been associated with exposure to ultraviolet radiation. They are particularly prevalent in persons who spend a lot of time in the sun, such as fisherman. There is nothing you can do to prevent the formation of cataracts.<\\/p>\\r\\n<p>Cataract treatment is advised when decreased vision affects your everyday activities or hobbies.<\\/p>\\r\\n<p>Cataract surgery, in which the normal cloudy lens is removed, is now a very successful procedure. The most widely used technique is called phacoemulsification. A very small incision is made and a tiny ultrasonic probe is used to break up the cataract and gently suction it away. A clear membrane is left in your eye where an intraocular lens is placed (IOL). This IOL is necessary to replace the focusing power of the natural lens, which was removed. With insertion of an IOL, there is little need for thick cataract glasses and contact lenses that were used years ago. Other type of lenses gaining popularity are MULTIFOCAL and ACCOMODATIVE. These IOLs may be used to treat myopia, hyperopia and presbyopia, and may allow less reliance on glasses and\\/or contact lenses to see clearly at both distance and near.<\\/p>\\r\\n<p>Small incision surgery has several benefits. The procedure is very quick, sometimes taking less than 20 minutes. Also, recovery time is short. The results of the surgery are almost immediate. Most people notice an improvement in their vision soon after surgery.<\\/p>\",\"fulltext\":\"\",\"state\":1,\"catid\":\"11\",\"created\":\"2015-01-27 05:51:07\",\"created_by\":\"42\",\"created_by_alias\":\"\",\"modified\":\"\",\"modified_by\":null,\"checked_out\":null,\"checked_out_time\":null,\"publish_up\":\"2015-01-27 05:51:07\",\"publish_down\":\"0000-00-00 00:00:00\",\"images\":\"{\\\"image_intro\\\":\\\"\\\",\\\"float_intro\\\":\\\"\\\",\\\"image_intro_alt\\\":\\\"\\\",\\\"image_intro_caption\\\":\\\"\\\",\\\"image_fulltext\\\":\\\"\\\",\\\"float_fulltext\\\":\\\"\\\",\\\"image_fulltext_alt\\\":\\\"\\\",\\\"image_fulltext_caption\\\":\\\"\\\"}\",\"urls\":\"{\\\"urla\\\":false,\\\"urlatext\\\":\\\"\\\",\\\"targeta\\\":\\\"\\\",\\\"urlb\\\":false,\\\"urlbtext\\\":\\\"\\\",\\\"targetb\\\":\\\"\\\",\\\"urlc\\\":false,\\\"urlctext\\\":\\\"\\\",\\\"targetc\\\":\\\"\\\"}\",\"attribs\":\"{\\\"show_title\\\":\\\"\\\",\\\"link_titles\\\":\\\"\\\",\\\"show_tags\\\":\\\"\\\",\\\"show_intro\\\":\\\"\\\",\\\"info_block_position\\\":\\\"\\\",\\\"show_category\\\":\\\"\\\",\\\"link_category\\\":\\\"\\\",\\\"show_parent_category\\\":\\\"\\\",\\\"link_parent_category\\\":\\\"\\\",\\\"show_author\\\":\\\"\\\",\\\"link_author\\\":\\\"\\\",\\\"show_create_date\\\":\\\"\\\",\\\"show_modify_date\\\":\\\"\\\",\\\"show_publish_date\\\":\\\"\\\",\\\"show_item_navigation\\\":\\\"\\\",\\\"show_icons\\\":\\\"\\\",\\\"show_print_icon\\\":\\\"\\\",\\\"show_email_icon\\\":\\\"\\\",\\\"show_vote\\\":\\\"\\\",\\\"show_hits\\\":\\\"\\\",\\\"show_noauth\\\":\\\"\\\",\\\"urls_position\\\":\\\"\\\",\\\"alternative_readmore\\\":\\\"\\\",\\\"article_layout\\\":\\\"\\\",\\\"show_publishing_options\\\":\\\"\\\",\\\"show_article_options\\\":\\\"\\\",\\\"show_urls_images_backend\\\":\\\"\\\",\\\"show_urls_images_frontend\\\":\\\"\\\",\\\"extra-class\\\":\\\"\\\"}\",\"version\":1,\"ordering\":null,\"metakey\":\"\",\"metadesc\":\"\",\"access\":\"1\",\"hits\":null,\"metadata\":\"{\\\"robots\\\":\\\"\\\",\\\"author\\\":\\\"\\\",\\\"rights\\\":\\\"\\\",\\\"xreference\\\":\\\"\\\"}\",\"featured\":\"0\",\"language\":\"*\",\"xreference\":\"\"}',0);
INSERT INTO `jos_ucm_history` VALUES (157,16,1,'','2015-01-27 05:52:08',42,2404,'c8789ca16ae06b8e1a37c8f2e55b9fa3dbe4aa23','{\"id\":16,\"asset_id\":84,\"title\":\"Glaucoma\",\"alias\":\"glaucoma\",\"introtext\":\"<p>Glaucoma is a disease that typically affects older people, although it can occur at any age.\\u00a0 It is the leading cause of blindness in the United States.<\\/p>\\r\\n<p>The eye has around 1 million tiny nerve fibers that run from the back of the eye to the brain.\\u00a0 These nerve fibers allow us to see. Glaucoma is a disease that causes the destruction of these fibers.<\\/p>\\r\\n<p>While glaucoma has the potential to cause blindness, loss of vision is preventable if the disease is detected and treated early.\\u00a0 Our glaucoma specialist can help in this process.\\u00a0 He treats all conditions associated with glaucoma and, when necessary, performs procedures such as medical laser, trabeculectomies, and glaucoma implant surgeries.<\\/p>\",\"fulltext\":\"\",\"state\":1,\"catid\":\"2\",\"created\":\"2015-01-27 05:52:08\",\"created_by\":\"42\",\"created_by_alias\":\"\",\"modified\":\"\",\"modified_by\":null,\"checked_out\":null,\"checked_out_time\":null,\"publish_up\":\"2015-01-27 05:52:08\",\"publish_down\":\"0000-00-00 00:00:00\",\"images\":\"{\\\"image_intro\\\":\\\"\\\",\\\"float_intro\\\":\\\"\\\",\\\"image_intro_alt\\\":\\\"\\\",\\\"image_intro_caption\\\":\\\"\\\",\\\"image_fulltext\\\":\\\"\\\",\\\"float_fulltext\\\":\\\"\\\",\\\"image_fulltext_alt\\\":\\\"\\\",\\\"image_fulltext_caption\\\":\\\"\\\"}\",\"urls\":\"{\\\"urla\\\":false,\\\"urlatext\\\":\\\"\\\",\\\"targeta\\\":\\\"\\\",\\\"urlb\\\":false,\\\"urlbtext\\\":\\\"\\\",\\\"targetb\\\":\\\"\\\",\\\"urlc\\\":false,\\\"urlctext\\\":\\\"\\\",\\\"targetc\\\":\\\"\\\"}\",\"attribs\":\"{\\\"show_title\\\":\\\"\\\",\\\"link_titles\\\":\\\"\\\",\\\"show_tags\\\":\\\"\\\",\\\"show_intro\\\":\\\"\\\",\\\"info_block_position\\\":\\\"\\\",\\\"show_category\\\":\\\"\\\",\\\"link_category\\\":\\\"