INSERT INTO `jos_ucm_history` VALUES (160,16,1,'','2015-01-27 05:53:06',42,2441,'3347c92dc39260036a1eb11e921610882ee3063c','{\"id\":16,\"asset_id\":\"84\",\"title\":\"Glaucoma\",\"alias\":\"glaucoma\",\"introtext\":\"<p>Glaucoma is a disease that typically affects older people, although it can occur at any age.\\u00a0 It is the leading cause of blindness in the United States.<\\/p>\\r\\n<p>The eye has around 1 million tiny nerve fibers that run from the back of the eye to the brain.\\u00a0 These nerve fibers allow us to see. Glaucoma is a disease that causes the destruction of these fibers.<\\/p>\\r\\n<p>While glaucoma has the potential to cause blindness, loss of vision is preventable if the disease is detected and treated early.\\u00a0 Our glaucoma specialist can help in this process.\\u00a0 He treats all conditions associated with glaucoma and, when necessary, performs procedures such as medical laser, trabeculectomies, and glaucoma implant surgeries.<\\/p>\",\"fulltext\":\"\",\"state\":1,\"catid\":\"11\",\"created\":\"2015-01-27 05:52:08\",\"created_by\":\"42\",\"created_by_alias\":\"\",\"modified\":\"2015-01-27 05:53:06\",\"modified_by\":\"42\",\"checked_out\":\"42\",\"checked_out_time\":\"2015-01-27 05:53:01\",\"publish_up\":\"2015-01-27 05:52:08\",\"publish_down\":\"0000-00-00 00:00:00\",\"images\":\"{\\\"image_intro\\\":\\\"\\\",\\\"float_intro\\\":\\\"\\\",\\\"image_intro_alt\\\":\\\"\\\",\\\"image_intro_caption\\\":\\\"\\\",\\\"image_fulltext\\\":\\\"\\\",\\\"float_fulltext\\\":\\\"\\\",\\\"image_fulltext_alt\\\":\\\"\\\",\\\"image_fulltext_caption\\\":\\\"\\\"}\",\"urls\":\"{\\\"urla\\\":false,\\\"urlatext\\\":\\\"\\\",\\\"targeta\\\":\\\"\\\",\\\"urlb\\\":false,\\\"urlbtext\\\":\\\"\\\",\\\"targetb\\\":\\\"\\\",\\\"urlc\\\":false,\\\"urlctext\\\":\\\"\\\",\\\"targetc\\\":\\\"\\\"}\",\"attribs\":\"{\\\"show_title\\\":\\\"\\\",\\\"link_titles\\\":\\\"\\\",\\\"show_tags\\\":\\\"\\\",\\\"show_intro\\\":\\\"\\\",\\\"info_block_position\\\":\\\"\\\",\\\"show_c