INSERT INTO `jos_ucm_history` VALUES (164,20,1,'','2015-01-27 05:58:45',42,2577,'73867d7cfffafc36791269ba1b40dee397631a5b','{\"id\":20,\"asset_id\":88,\"title\":\"Optical\",\"alias\":\"optical\",\"introtext\":\"<p>Visit our state-of-the-art optical department featuring the \\\"Smart Mirror,\\\" an exciting new imaging system that takes the guesswork out of choosing your eyewear, by letting you see what your new glasses will look like. Our expert certified opticians are skilled in fitting the latest lens technology (i.e. Varilux progressive no-line, Thin &amp; Light Lenses, Transitions) and frames including Gucci, Nine West, Fossil, Laura Ashley, Cover Girl, Armani, Ferragamo, Ralph Lauren and many more, all at competitive pricing. Our frame stylist will help your choose from our wide selection of rimless\\/drill mount, metal plastic and designer frames, including magnetic and clip on sun glasses.<\\/p>\\r\\n<p>Enjoy personal attention, knowledgeable staff, a friendly professional atmosphere, no sales pressure and our personal satisfaction guarantee. Let us help you make selecting your eyewear an enjoyable experience.<\\/p>\",\"fulltext\":\"\",\"state\":1,\"catid\":\"11\",\"created\":\"2015-01-27 05:58:45\",\"created_by\":\"42\",\"created_by_alias\":\"\",\"modified\":\"\",\"modified_by\":null,\"checked_out\":null,\"checked_out_time\":null,\"publish_up\":\"2015-01-27 05:58:45\",\"publish_down\":\"0000-00-00 00:00:00\",\"images\":\"{\\\"image_intro\\\":\\\"\\\",\\\"float_intro\\\":\\\"\\\",\\\"image_intro_alt\\\":\\\"\\\",\\\"image_intro_caption\\\":\\\"\\\",\\\"image_fulltext\\\":\\\"\\\",\\\"float_fulltext\\\":\\\"\\\",\\\"image_fulltext_alt\\\":\\\"\\\",\\\"image_fulltext_caption\\\":\\\"\\\"}\",\"urls\":\"{\\\"urla\\\":false,\\\"urlatext\\\":\\\"\\\",\\\"targeta\\\":\\\"\\\",\\\"urlb\\\":false,\\\"urlbtext\\\":\\\"\\\",\\\"targetb\\\":\\\"\\\",\\\"urlc\\\":false,\\\"urlctext\\\":\\\"\\\",\\\"targetc\\\":\\\"\\\"}\",\"attribs\":\"{\\\"show_title\\\":\\\"\\\",\\\"lin